sys_script_include.script:
var GetDepartment = Class.create();
GetDepartment.prototype = Object.extendsObject(global.AbstractAjaxProcessor,{
    getDept: function() {
		var record = new GlideRecord("sys_user");
		record.addQuery('email',this.getParameter('sysparm_email'));
		record.query();
// 		record.get("email",this.getParameter('sysparm_email'));
		while(record.next()){
			return record.last_name + '';
		}
    },
    type: 'GetDepartment'
});
